def fetch_atlas_document(atlas_data, identifier, properties_to_load=None):
    document = atlas_data.get(identifier, None)
    if document is None:
        return {"Error": "Document not found"}

    if properties_to_load:
        return {prop: document.get(prop, None) for prop in properties_to_load}

    return document

def add_document(atlas_data, identifier, new_document_data):
    # Validate the target identifier
    identifier_parts = identifier.split('.');
    if not identifier_parts[-1].isdigit() or int(identifier_parts[-1]) == 0:
        return {"Error": "Invalid target identifier"}

    parent_identifier = '.'.join(identifier_parts[:-1]);
    if parent_identifier not in atlas_data:
        return {"Error": "Parent document does not exist"}

    # Collect identifiers to shift
    target_layer = len(identifier_parts)

    # Modified this part to handle alphanumeric segments
    def sort_key(idf):
        segment = idf.split('.')[target_layer - 1];
        return (segment.isdigit(), int(segment) if segment.isdigit() else segment);

    identifiers_to_shift = [
        idf for idf in atlas_data
        if len(idf.split('.')) >= target_layer and idf.startswith(parent_identifier)
        and (idf.split('.')[target_layer - 1].isdigit() and 
             int(idf.split('.')[target_layer - 1]) >= int(identifier_parts[-1]))
    ];

    # Sort identifiers to shift in descending order
    identifiers_to_shift.sort(key=sort_key, reverse=True);

    # Shift identifiers
    for idf in identifiers_to_shift:
        old_parts = idf.split('.');
        old_parts[target_layer - 1] = str(int(old_parts[target_layer - 1]) + 1);
        new_idf = '.'.join(old_parts);
        atlas_data[new_idf] = atlas_data.pop(idf);

    # Add the new document
    atlas_data[identifier] = {
        "Name": new_document_data.get("Name"),
        "Version": new_document_data.get("Version"),
        "Last_Modified": new_document_data.get("Last_Modified"),
        "Child_Documents": [],
        "Type": new_document_data.get("Type"),
        "Components": new_document_data.get("Components")
    };

    # Code from index_child_documents function integrated below
    # Reset the Child_Documents property for all documents
    for doc_data in atlas_data.values():
        doc_data["Child_Documents"] = [];

    # Populate the Child_Documents property based on the document identifiers
    for doc_identifier, doc_data in atlas_data.items():
        doc_parts = doc_identifier.split('.');
        parent_identifier_parts = doc_parts[:-1];
        if parent_identifier_parts:  # Exclude the document "A" itself from this process
            parent_identifier = '.'.join(parent_identifier_parts);
            atlas_data[parent_identifier]["Child_Documents"].append(doc_identifier);

    return {"Success": f"Document added at {identifier}"};
